const path = require('path');
const webpack = require('webpack');
const merge = require('webpack-merge');
const webpackBaseConfig = require('./webpack.config.base');
const CopyWebpackPlugin = require('copy-webpack-plugin');

const config = require('./index');

module.exports = merge(webpackBaseConfig, {
  context: config.source.root,
  output: {
    path: config.build.assetsRoot + config.build.assetsPublicPath,
    filename: '[name]' + config.build.version + '.min.js'
  },
  plugins: [
    new webpack.DefinePlugin({
      'process.env': {
        NODE_ENV: process.env.NODE_ENV || '"production"'
      }
    }),
    new webpack.optimize.UglifyJsPlugin({
      sourceMap: true,
      comments: false, // 去掉注释内容
      output: {
        comments: false // 去掉注释内容
      },
      compress: {
        warnings: false //忽略警告,
      }
    }),
    new webpack.LoaderOptionsPlugin({
      minimize: true
    }),
    // copy custom static assets
    new CopyWebpackPlugin([
      {
        from: config.source.root + config.source.static,
        to: config.build.assetsRoot + config.build.assetsPublicPath,
        ignore: ['.*']
      }
    ])
  ],
  devtool: '#cheap-module-source-map'
});
